(437, 244, 'JSN Artista Demo CSS', '', '<style type="text/css">\r\n/* ========== PROMO ========== */\r\n.jsn-homepage.jsn-demo-page #jsn-promo {\r\n	margin: 0;\r\n}\r\n.jsn-homepage.jsn-demo-page .box_skitter,\r\n.jsn-homepage.jsn-demo-page .label_skitter {\r\n	background: none !important;\r\n}\r\n\r\n/* ========== MAIN FEATURES ========== */\r\n#demo-main-features {\r\n	padding: 80px 0;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col div.grid-col_inner {\r\n	margin-right: 0;\r\n	padding: 55px;\r\n}\r\n\r\n#demo-main-features div.grid-layout.grid1 div.grid-col.block1 div.grid-col_inner {\r\n	border-right: 1px solid #e5e5e5;\r\n	border-bottom: 1px solid #e5e5e5;\r\n}\r\n#demo-main-features div.grid-layout.grid2 div.grid-col.block4 div.grid-col_inner {\r\n	border-top: 1px solid #e5e5e5;\r\n	border-left: 1px solid #e5e5e5;\r\n}\r\n#demo-main-features div.grid-layout.grid2 div.grid-col {\r\n	margin-top: -1px;\r\n}\r\n#demo-main-features div.grid-layout.grid2 div.grid-col.block4 {\r\n	margin-left: -1px;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .icon {\r\n	display: inline-block;\r\n	float: left;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .icon .link-icon {\r\n	display: block;\r\n	padding: 0;\r\n	width: 63px;\r\n	height: 63px;\r\noverflow: hidden;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\ndisplay: block;\r\n	background-color: #555;\r\n}\r\n.jsn-color-blue #demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\n	background-color: #009aca;\r\n}\r\n.jsn-color-red #demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\n	background-color: #bf0000;\r\n}\r\n.jsn-color-green #demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\n	background-color: #429e00;\r\n}\r\n.jsn-color-orange #demo-main-features div.grid-layout div.grid-col .icon .link-icon img  {\r\n	background-color: #e66000;\r\n}\r\n.jsn-color-violet #demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\n	background-color: #9e0084;\r\n}\r\n.jsn-color-grey #demo-main-features div.grid-layout div.grid-col .icon .link-icon img {\r\n	background-color: #555;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .intro-content {\r\n	padding-left: 90px;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .intro-content .intro-heading a {\r\n	color: #000;\r\n	font-weight: bold;\r\n}\r\n#demo-main-features div.grid-layout div.grid-col .intro-content .desc {\r\n	color: #888;\r\n	line-height: 2em;\r\n}\r\n\r\n/* ========== MAIN CONTENT ========== */\r\n/* ========== DEMO GALLERY ========== */\r\n#demo-gallery {\r\n	padding: 50px 0;\r\n	border-bottom: 1px solid #e5e5e5;\r\n}\r\n#demo-gallery .item {\r\n	padding: 40px;\r\n}\r\n#demo-gallery .item .img a {\r\n	display: block;\r\n	position: relative;\r\n}\r\n#demo-gallery .item .img a img {\r\n	max-width: 100%;\r\n}\r\n#demo-gallery .item .desc {\r\n	padding: 10px 20px;\r\n	border: 1px solid #e5e5e5;\r\n}\r\n#demo-gallery .item .img a:before {\r\n	content: "";\r\n	display: block;\r\n	position: absolute;\r\n	width: 70px;\r\n	height: 70px;\r\n	top: calc(50% - 15px);\r\n	left: 50%;\r\n	margin-left: -35px;\r\n	background: #000 url(http://demo.joomlashine.com/joomla-templates/jsn_artista/free/media/joomlashine/jsn-artista/custom-html/demo-gallery/icon-zoom.png) center center no-repeat;\r\n	z-index: 10;\r\n	opacity: 0;\r\n	filter: alpha(opacity=0);\r\n	-ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";\r\n	border-radius: 100%;\r\n	-moz-border-radius: 100%;\r\n	-webkit-border-radius: 100%;\r\n	transition: all 0.2s ease-in-out;\r\n	-moz-transition: all 0.2s ease-in-out;\r\n	-webkit-transition: all 0.2s ease-in-out;\r\n}\r\n#demo-gallery .item .img a:hover:before {\r\n	top: calc(50% - 35px);\r\n	opacity: 1;\r\n	filter: alpha(opacity=100);\r\n	-ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";\r\n}\r\n#demo-gallery .item .img a:after{\r\n	content: "";\r\n	position: absolute;\r\n	display: block;\r\n	width: 100%;\r\n	height: 100%;\r\n	top: 0;\r\n	left: 0;\r\n	background-color: rgba(0,0,0,0.3);\r\n	z-index: 9;\r\n	opacity: 0;\r\n	filter: alpha(opacity=0);\r\n	-ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=0)";\r\n	transition: all 0.2s ease-in-out;\r\n	-moz-transition: all 0.2s ease-in-out;\r\n	-webkit-transition: all 0.2s ease-in-out;\r\n}\r\n#demo-gallery .item .img a:hover:after {\r\n	opacity: 1;\r\n	filter: alpha(opacity=100);\r\n	-ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=100)";\r\n}\r\n\r\n/* ========== USERMODULES3 ========== */\r\n.jsn-homepage.jsn-demo-page #jsn-pos-content-bottom + #jsn-usermodules3 {\r\n	padding: 50px 0;\r\n}\r\n.jsn-homepage.jsn-demo-page div.jsn-modulecontainer ul.menu-sidemenu {\r\n	border: none;\r\n}\r\n.jsn-homepage.jsn-demo-page #jsn-usermodules3 ul.menu-sidemenu > li > a > span {\r\n	padding-left: 0;\r\n	background: none;\r\n}\r\n\r\n@media only screen and (max-width: 768px), (max-device-width: 768px) {\r\n	#demo-main-features div.grid-layout div.grid-col div.grid-col_inner {\r\n		padding: 30px;\r\n	}\r\n	#demo-main-features {\r\n		padding: 50px 0;\r\n	}\r\n	#demo-gallery .item .img a {\r\n		right: auto;\r\n		left: auto;\r\n		top: auto;\r\n	}\r\n}\r\n@media only screen and (max-width: 480px), (max-device-width: 480px) {\r\n	[class*="jsn-themeslider-skitter-"] .label_skitter p.jsn-themeslider-caption-title {\r\n		font-size: 26px;\r\n		margin-top: 30px;\r\n	}\r\n	[class*="jsn-themeslider-skitter-"] .label_skitter p.jsn-themeslider-caption-description {\r\n		font-size: 16px;\r\n	}\r\n	#demo-main-features div.grid-layout.grid1 div.grid-col.block1 div.grid-col_inner {\r\n		border-bottom: none;\r\n		border-right: none;\r\n	}\r\n	#demo-main-features div.grid-layout.grid2 div.grid-col.block4 div.grid-col_inner {\r\n		border-left: none;\r\n		border-top: none;\r\n	}\r\n	#demo-main-features div.grid-layout div.grid-col div.grid-col_inner {\r\n		padding: 20px;\r\n	}\r\n}\r\n</s